function getCookie(e){let t=e+"=",n=decodeURIComponent(document.cookie).split(";");for(let e=0;e<n.length;e++){let d=n[e];for(;" "==d.charAt(0);)d=d.substring(1);if(0==d.indexOf(t))return d.substring(t.length,d.length)}return""}function checkCookie(){let e=getCookie("Date"),t=getCookie("Event");""!=e?(document.getElementById("dateInput").value=e,document.getElementById("eventInput").value=t,daysRemaining()):document.getElementById("pre_calc").classList.remove("hidden")}function setCookie(e,t,n){const d=new Date;d.setTime(d.getTime()+24*n*60*60*1e3);let o="expires="+d.toUTCString();document.cookie=e+"="+t+";"+o+";path=/"}function daysRemaining(){if(""==document.getElementById("dateInput").value)return window.alert("Please Enter a date");if(""==document.getElementById("eventInput").value)return window.alert("Please Enter an event name");const e=new Date;setCookie("Date",t=document.getElementById("dateInput").value,365);var t,n=document.getElementById("eventInput").value,d=e,o=(t=new Date(t)).getTime()-d.getTime(),i=" days",l=Math.ceil(o/864e5);if(1==l)i=" day";return setCookie("Event",n,365),document.getElementById("result").innerHTML=l+i,document.getElementById("event_name").innerHTML=n,document.title=l+i+" till "+n,document.getElementById("pre_calc").classList.add("hidden"),document.getElementById("post_calc").classList.remove("hidden")}function changeSetting(){document.getElementById("pre_calc").classList.remove("hidden"),document.getElementById("post_calc").classList.add("hidden"),document.title="Days Till Date Calculator"}